mapper.select:
select
    wn.id as id,
    wn.name as name,
    wn.ip as ip,
    wn.port as port,
    wn.root_path as rootPath
from (
    select
        distinct node_id
    from model_file
    where
        1=1

        and

            (
                model_file.institute=#{model.institute}
                and model_file.model=#{model.model}
                and model_file.experiment=#{model.experiment}
                and model_file.frequency=#{model.frequency}
                and model_file.modeling_realm=#{model.modelingRealm}
                and model_file.ensemble_member=#{model.ensembleMember}
                and model_file.variable_name=#{model.variableName}
            )



) t
join worker_node wn on t.node_id=wn.id

sql.foreach:
(
model_file.institute=#{model.institute}
and model_file.model=#{model.model}
and model_file.experiment=#{model.experiment}
and model_file.frequency=#{model.frequency}
and model_file.modeling_realm=#{model.modelingRealm}
and model_file.ensemble_member=#{model.ensembleMember}
and model_file.variable_name=#{model.variableName}
)

mapper.select:
select distinct

from model_file where
	1=1

	and institute = #{model.institute}
	and model = #{model.model}
	and experiment = #{model.experiment}
	and frequency = #{model.frequency}
	and modeling_realm = #{model.modelingRealm}
	and ensemble_member = #{model.ensembleMember}
	and variable_name = #{model.variableName}
order by


	desc
	asc

limit 0, 1

		select 
			full_path
		from model_file
		where
            model_file.institute=#{model.institute}
            and model_file.model=#{model.model}
            and model_file.experiment=#{model.experiment}
            and model_file.frequency=#{model.frequency}
            and model_file.modeling_realm=#{model.modelingRealm}
            and model_file.ensemble_member=#{model.ensembleMember}
            and model_file.variable_name=#{model.variableName}
