mapper.update:
update sub_task set
	progress=#{progress},
	progress_description=#{description}
where id=#{id}

update sub_task set
	status=#{status},
	progress_description=#{description}
where id=#{id}

update sub_task set
    status='failed',
    failure_cause=#{failureCause}
where id=#{id}

mapper.insert:
insert into sub_task_result (
	sub_task_id,
	type,
	path
) values (
	#{id},
	#{type},
	#{path}
)
